
$z = '0287afa306cd2267665eb4442566561a6f4a823e496ee0d723bf5d67b88f257b9f';

$key = PublicKeyFactory::fromHex($z);

function decompressKey($key) {

    $ecAdapter = Bitcoin::getEcAdapter();
    
    return new PublicKey(
        $ecAdapter,
        $key->getPoint(),
        false,
        $key->getPrefix()
    );
    
}

$t = decompressKey($key);

echo $z. "\n";
echo $t->getHex() . "\n";`
