var crypto = require('crypto');
var pbkdf2Sha256 = function(pw, salt, iterations, keySizeBytes) {
  return crypto.pbkdf2Sync(pw, salt, iterations, keySizeBytes,'sha256');
};
var pbkdf2Sha512 = function(pw, salt, iterations, keySizeBytes) {
  return crypto.pbkdf2Sync(pw, salt, iterations, keySizeBytes, 'sha512');
};

var pw = new Buffer('password')
var salt = new Buffer('41414141','hex')
var iterations = 35000
var keySizeBytes = 32

console.log(pbkdf2Sha256(pw, salt, iterations, keySizeBytes))
console.log(pbkdf2Sha512(pw, salt, iterations, keySizeBytes))
